// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

contract Lottery {
    uint public lotteryBalance;
    uint public totalTickets;
    uint public ticketPrice;
    uint public ticketsToBuy;
    address public owner;
    mapping(address => uint) public players;

    constructor(){

        owner = msg.sender;
        totalTickets = 0;
        lotteryBalance = 0;
    }

    modifier onlyOwner{
        require(msg.sender == owner, "Only owner can call");
        _;
    }

    function buyTicket() public payable{
        require(msg.value == ticketPrice, "Send correct amount");
        players[msg.sender] += ticketsToBuy;
        totalTickets += ticketsToBuy;
        lotteryBalance += msg.value;
    }

   // function drawWinner() public onlyOwner{
     //   require(totalTickets > 0, "No tickets sold");
    //    address winner;
     //   uint prize = lotteryBalance * 90/100;

   // }

}
